mapper.select:
select 

from item_item_comment
where createTime = #{createtime,jdbcType=TIMESTAMP}
  and fromUserId = #{fromuserid,jdbcType=INTEGER}

mapper.delete:
delete from item_item_comment
where createTime = #{createtime,jdbcType=TIMESTAMP}
  and fromUserId = #{fromuserid,jdbcType=INTEGER}

mapper.insert:
insert into item_item_comment (createTime, fromUserId, toUserId, 
  itemId, `state`, `comment`
  )
values (#{createtime,jdbcType=TIMESTAMP}, #{fromuserid,jdbcType=INTEGER}, #{touserid,jdbcType=INTEGER}, 
  #{itemid,jdbcType=INTEGER}, #{state,jdbcType=INTEGER}, #{comment,jdbcType=LONGVARCHAR}
  )

mapper.update:
update item_item_comment
set toUserId = #{touserid,jdbcType=INTEGER},
  itemId = #{itemid,jdbcType=INTEGER},
  `state` = #{state,jdbcType=INTEGER},
  `comment` = #{comment,jdbcType=LONGVARCHAR}
where createTime = #{createtime,jdbcType=TIMESTAMP}
  and fromUserId = #{fromuserid,jdbcType=INTEGER}

update item_item_comment
set toUserId = #{touserid,jdbcType=INTEGER},
  itemId = #{itemid,jdbcType=INTEGER},
  `state` = #{state,jdbcType=INTEGER}
where createTime = #{createtime,jdbcType=TIMESTAMP}
  and fromUserId = #{fromuserid,jdbcType=INTEGER}
