mapper.sql:
from user_user

	1 = 1

		and state = #{state}


		and nickname = #{nickname}


		and mobile = #{mobile}


		and openid = #{openid}


		and unionId = #{unionId}

mapper.update:
update item_item_comment set state = 1 where fromUserId =
#{fromUserId}

	and toUserId = #{toUserId}


	and itemId = #{itemId}


	and createTime between #{startTime} and #{endTime}

mapper.select:
select unionid from user_user
where 1 = 1

	and state = #{state}


	and nickname = #{nickname}


	and mobile = #{mobile}


	and openid = #{openid}


	and unionId = #{unionId}

group by unionId

select * from
user_user where unionId = #{unionId} order by lastModifyTime desc
limit 1;
